mapper.select:
SELECT  *  FROM  sys_permission


    and PermissionCode = #{permissionCode}


    and ParentID = #{parentID}


    and `PermissionName` like concat('%',#{permissionName},'%')




    order by ${sidx} ${order}


    order by CreateDate asc

SELECT  COUNT(1)  FROM  sys_permission


    and PermissionCode = #{permissionCode}


    and ParentID = #{parentID}


    and `PermissionName` like concat('%',#{permissionName},'%')

